    for (size_t i = 0; i < layers.size(); ++i) {
        this->layers.push_back(std::vector<Neuron>(layers[i]));
        if (i > 0) {
            weights.push_back(std::vector<std::vector<double>>(layers[i], std::vector<double>(layers[i - 1])));
