#include <iostream>
#include <vector>
#include <cmath>
#include <random>

// Node: Represents a single unit in the network
struct Node {
    double value; // Current value of the node
    double bias;  // Bias value to adjust the output

    Node(double v = 0.0, double b = 0.0) : value(v), bias(b) {}

    // Activation function
    double activate() {
        return 1.0 / (1.0 + exp(-(value + bias))); // Sigmoid activation
    }
};

// Link: Connects two nodes with a weight
struct Link {
    int from;  // Index of the source node
    int to;    // Index of the target node
    double weight; // Weight of the link

    Link(int f, int t, double w) : from(f), to(t), weight(w) {}
};

// Neural Network: A collection of nodes and links
struct Network {
    std::vector<Node> nodes;   // All nodes in the network
    std::vector<Link> links;   // All links between nodes

    // Add a node
    int addNode(double value = 0.0, double bias = 0.0) {
        nodes.emplace_back(value, bias);
        return nodes.size() - 1;
    }

    // Add a link
    void addLink(int from, int to, double weight) {
        links.emplace_back(from, to, weight);
    }

    // Update the network values
    void update() {
        for (auto &link : links) {
            nodes[link.to].value += nodes[link.from].activate() * link.weight;
        }
        for (auto &node : nodes) {
            node.value = node.activate();
        }
    }

    // Display the network state
    void display() {
        std::cout << "Nodes:\n";
        for (size_t i = 0; i < nodes.size(); ++i) {
            std::cout << "Node " << i << ": Value = " << nodes[i].value 
                      << ", Bias = " << nodes[i].bias << "\n";
        }
        std::cout << "Links:\n";
        for (const auto &link : links) {
            std::cout << "From Node " << link.from << " to Node " << link.to 
                      << ": Weight = " << link.weight << "\n";
        }
    }
};

int main() {
    Network net;

    // Create nodes
    int t = net.addNode(1.0, 0.1); // Time node
    int d = net.addNode(0.5, -0.1); // Distance node
    int f = net.addNode(0.0, 0.0); // Force node

    // Create links
    net.addLink(t, f, 0.8);
    net.addLink(d, f, 0.5);

    // Update and display network
    net.update();
    net.display();

    return 0;
}
```

