        char buffer[8192];
        std::string lineBuffer;

        while (int bytesRead = gzread(file, buffer, sizeof(buffer))) {
            for (int i = 0; i < bytesRead; ++i) {
                if (buffer[i] == '\n') {
                    parseLine(lineBuffer);
                    lineBuffer.clear();
                } else {
                    lineBuffer += buffer[i];
                }
            }
        }

        gzclose(file);
    }

