#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <filesystem>
#include <map>
#include <set>
#include <unordered_set>
#include <zlib.h> // For gzip file handling
#include <cctype>
#include <algorithm>
#include <sstream>

namespace fs = std::filesystem;

// Struct to store track metadata
struct Track {
    std::string path;
    std::string artist;
    std::string title;
    std::string genre;
    std::string category; // Daytime or Nighttime
};

// Containers for playlists, stats, skipped tracks, and relevant albums
std::vector<Track> daytimePlaylist;
std::vector<Track> nighttimePlaylist;
std::vector<std::string> skippedTracks;
std::map<std::string, std::string> albumGenres; // Map album title to genre
std::map<std::string, int> genreStats;         // Genre distribution
std::set<std::string> relevantAlbums;          // Albums found in the library
int totalTracks = 0;                           // Total tracks processed
int skippedCount = 0;                          // Count of skipped tracks

// Define genres for categorization using unordered_set
const std::unordered_set<std::string> DAYTIME_GENRES = {"Rock", "Pop", "Electronic", "Dance", "Hip-Hop", "Upbeat"};
const std::unordered_set<std::string> NIGHTTIME_GENRES = {"Classical", "Instrumental", "Acoustic", "Relaxing", "Ambient"};

// Normalize a string by trimming and replacing multiple spaces with a single space
std::string normalizeString(const std::string &input) {
    std::string output;
    bool lastWasSpace = false;

    for (char c : input) {
        if (std::isspace(static_cast<unsigned char>(c))) {
            if (!lastWasSpace) {
                output += ' ';
                lastWasSpace = true;
            }
        } else {
            output += std::tolower(static_cast<unsigned char>(c));
            lastWasSpace = false;
        }
    }

    // Trim leading/trailing spaces
    if (!output.empty() && output.front() == ' ') output.erase(output.begin());
    if (!output.empty() && output.back() == ' ') output.erase(output.end() - 1);

    return output;
}

// Helper: Multi-word similarity function
int multiWordSimilarity(const std::string &a, const std::string &b) {
    std::istringstream streamA(a), streamB(b);
    std::unordered_set<std::string> wordsA, wordsB;
    std::string word;
    int score = 0;

    // Split strings into words
    while (streamA >> word) {
        wordsA.insert(word);
    }
    while (streamB >> word) {
        wordsB.insert(word);
    }

    // Calculate overlap between word sets
    for (const auto &w : wordsA) {
        if (wordsB.count(w)) {
            score += w.size(); // Longer words contribute more
        }
    }
    return score;
}

// Function to extract content between XML tags
std::string extractTagContent(const std::string &line, const std::string &tag) {
    size_t start = line.find("<" + tag + ">");
    size_t end = line.find("</" + tag + ">");
    if (start == std::string::npos || end == std::string::npos) return "";
    start += tag.length() + 2; // Move past the opening tag
    return line.substr(start, end - start);
}

// Function to build a set of relevant albums from the music library
void buildRelevantAlbums(const std::string &musicDir) {
    int relevantCount = 0;

    for (const auto &entry : fs::recursive_directory_iterator(musicDir)) {
        if (entry.is_regular_file() &&
            (entry.path().extension() == ".mp3" || entry.path().extension() == ".flac" ||
             entry.path().extension() == ".wav" || entry.path().extension() == ".aac" ||
             entry.path().extension() == ".ogg" || entry.path().extension() == ".m4a")) {
            std::string album = entry.path().parent_path().filename().string(); // Use folder name as album
            relevantAlbums.insert(album);
            relevantCount++;
        }
    }

    std::cout << "Total Relevant Albums Found: " << relevantCount << "\n";
}

// Function to load genres from a gzipped albums.xml.gz file
void loadAlbumsData(const std::string &filePath) {
    gzFile file = gzopen(filePath.c_str(), "r");
    if (!file) {
        std::cerr << "Failed to open file: " << filePath << "\n";
        return;
    }

    char buffer[8192];
    std::string line, album, genre, style;
    int albumCount = 0;

    while (gzgets(file, buffer, sizeof(buffer)) != Z_NULL) {
        line = buffer;

        // Extract album title
        if (line.find("<title>") != std::string::npos && line.find("<video") == std::string::npos) {
            album = extractTagContent(line, "title");
            album = normalizeString(album);
        }
        // Extract genre
        else if (line.find("<genre>") != std::string::npos) {
            genre = extractTagContent(line, "genre");
            genre = normalizeString(genre);
        }
        // Extract style (if genre is not specific enough)
        else if (line.find("<style>") != std::string::npos) {
            style = extractTagContent(line, "style");
            style = normalizeString(style);
        }
        // Extract track details from <video>
        else if (line.find("<video") != std::string::npos) {
            std::string videoTitle = extractTagContent(line, "title");
            videoTitle = normalizeString(videoTitle);

            if (!album.empty() && (!genre.empty() || !style.empty()) && !videoTitle.empty()) {
                std::string combinedKey = normalizeString(videoTitle + " " + album);
                std::string assignedGenre = !genre.empty() ? genre : style;
                albumGenres[combinedKey] = assignedGenre;
                albumCount++;
            }
        }

        // Reset on album boundary
        if (line.find("</master>") != std::string::npos) {
            album.clear();
            genre.clear();
            style.clear();
        }
    }

    gzclose(file);
    std::cout << "Total Albums Loaded: " << albumCount << "\n";
}



// Function to categorize tracks based on album and genre
void categorizeTrack(const fs::path &file) {
    totalTracks++;

    // Extract filename and album name
    std::string fileName = normalizeString(file.stem().string());
    std::string albumName = normalizeString(file.parent_path().filename().string());
    std::string filePath = file.string();
    std::string matchedGenre;

    // Combine album and title for similarity check
    std::string combinedKey = fileName + " " + albumName;

    // Exact match first
    if (albumGenres.find(combinedKey) != albumGenres.end()) {
        matchedGenre = albumGenres[combinedKey];
    } else {
        // Fallback to closest match
        std::string bestMatch;
        int bestScore = 0;

        for (const auto &entry : albumGenres) {
            int score = multiWordSimilarity(combinedKey, entry.first);
            if (score > bestScore) {
                bestScore = score;
                bestMatch = entry.first;
                matchedGenre = entry.second;
            }
        }

        if (bestScore < 10) {
            std::cout << "Weak Match for: " << combinedKey << " Closest Match: " << bestMatch
                      << " (Score: " << bestScore << "), Genre: " << matchedGenre << "\n";
        }
    }

    if (!matchedGenre.empty()) {
        if (DAYTIME_GENRES.count(matchedGenre)) {
            daytimePlaylist.push_back({filePath, "", fileName, matchedGenre, "Daytime"});
        } else if (NIGHTTIME_GENRES.count(matchedGenre)) {
            nighttimePlaylist.push_back({filePath, "", fileName, matchedGenre, "Nighttime"});
        }
    } else {
        skippedTracks.push_back(filePath);
        skippedCount++;
    }
}


// Function to scan the music directory
void scanMusicDirectory(const std::string &musicDir) {
    for (const auto &entry : fs::recursive_directory_iterator(musicDir)) {
        if (entry.is_regular_file() &&
            (entry.path().extension() == ".mp3" || entry.path().extension() == ".flac" ||
             entry.path().extension() == ".wav" || entry.path().extension() == ".aac" ||
             entry.path().extension() == ".ogg" || entry.path().extension() == ".m4a")) {
            categorizeTrack(entry.path());
        }
    }
}

// Function to write playlists to files
void writePlaylist(const std::string &filePath, const std::vector<Track> &playlist) {
    std::ofstream file(filePath);
    if (file.is_open()) {
        for (const auto &track : playlist) {
            file << track.path << "\n";
        }
        file.close();
        std::cout << "Playlist saved to: " << filePath << "\n";
    } else {
        std::cerr << "Failed to write playlist: " << filePath << "\n";
    }
}

// Function to print statistics
void printStats() {
    std::cout << "\nStatistics:\n";
    std::cout << "Total tracks processed: " << totalTracks << "\n";
    std::cout << "Daytime playlist: " << daytimePlaylist.size() << " tracks\n";
    std::cout << "Nighttime playlist: " << nighttimePlaylist.size() << " tracks\n";
    std::cout << "Skipped tracks: " << skippedCount << "\n";
    std::cout << "\nGenre breakdown:\n";
    for (const auto &entry : genreStats) {
        std::cout << entry.first << ": " << entry.second << " tracks\n";
    }
}

int main(int argc, char* argv[]) {
    // Default values
    std::string musicDir = ".";
    std::string albumsFilePath = "albums.xml.gz";

    // Override defaults if arguments are provided
    if (argc >= 2) {
        musicDir = argv[1];
    }
    if (argc >= 3) {
        albumsFilePath = argv[2];
    }

    std::cout << "Music Directory: " << musicDir << "\n";
    std::cout << "Albums File: " << albumsFilePath << "\n";

    // Build relevant albums and load albums data
    buildRelevantAlbums(musicDir);
    loadAlbumsData(albumsFilePath);

    // Scan music directory
    scanMusicDirectory(musicDir);

    // Write playlists and skipped tracks
    writePlaylist(musicDir + "/Daytime.m3u", daytimePlaylist);
    writePlaylist(musicDir + "/Nighttime.m3u", nighttimePlaylist);

    std::ofstream skippedFile(musicDir + "/skipped_tracks.log");
    if (skippedFile.is_open()) {
        for (const auto &track : skippedTracks) {
            skippedFile << track << "\n";
        }
        skippedFile.close();
    }

    printStats();

    std::cout << "\nPlaylists and logs generated successfully!\n";
    return 0;
}

